import React from "react";
import { Grid, Image, Icon } from "semantic-ui-react";
import { Transition } from "react-transition-group";

const duration = 300;

const defaultStyle = {
  transition: `opacity ${duration}ms ease-in-out`,
  opacity: 0,
};
const transitionStyles = {
  entering: { opacity: 0 },
  entered: { opacity: 0 },
  exiting: { opacity: 1 },
  exited: { opacity: 1 },
};

export default function FavoriteArtistCard({ artist, in: inProp }) {
  return (
    <Transition in={inProp} timeout={duration}>
      {(state) => (
        <Grid.Column
          style={{
            ...defaultStyle,
            ...transitionStyles[state],
          }}
        >
          <div className="favorite-wrapper">
            <Image
              className="favorite-image-wrapper"
              src={artist.images.length > 0 ? artist.images[1].url : null}
              // wrapped
            />

            <div className="artist-name-bg"></div>
            <div className="artist-name">{artist.name}</div>
            <a target="BLANK" href={artist.external_urls.spotify}>
              <Icon
                className="artist-play-icon"
                color="pink"
                size="big"
                name="play circle outline"
                onClick={() => console.log("what")}
              />
            </a>
          </div>
        </Grid.Column>
      )}
    </Transition>
  );
}
