import React, { useState, useEffect } from "react";
import Login from "./components/Login";
import Playlist from "./components/Playlist";
import Spotify from "./utils/spotify";
import "./styles/app.scss";

function App() {
  const [accessToken, setAccessToken] = useState(null);
  const [myDetails, setMyDetails] = useState({});

  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [playlist, setPlaylist] = useState([]);

  const getAccessToken = () => {
    const results = Spotify.getAccessToken();
    setAccessToken(results);
  };
  const getDetails = () => {
    const results = Spotify.getMyDetails().then((results) => {
      console.log(results);
    });
    setMyDetails(results);
  };

  const searchHandler = (e) => {
    e.preventDefault();
    console.log(e.target.value);
    setSearchTerm(e.target.value);
  };

  const search = (e) => {
    e.preventDefault();
    Spotify.search(searchTerm).then((results) => {
      console.log(results);
      setSearchResults(results);
    });
  };

  const findSimilar = (id) => {
    e.preventDefault();
    Spotify.findSimilar(id);
  };

  const makePlaylist = () => {
    let name = "test Playlist 12312";
    let songURI = [playlist.uri];
    Spotify.savePlaylist(name, songURI);
  };

  return (
    <div className="App">
      <button onClick={getAccessToken}>Login</button>
      <button onClick={getDetails}>getDetails</button>
      <button onClick={makePlaylist}>makePlaylist</button>

      <form>
        <input placeholder="search" onChange={(e) => searchHandler(e)}></input>
        <button onClick={search}>search</button>
        {searchTerm}
      </form>

      <ul>
        {searchResults &&
          searchResults.map((song) => {
            return (
              <li key={song.id} onClick={() => setPlaylist(song)}>
                {song.name}
              </li>
            );
          })}
      </ul>
      <div style={{ border: "2px solid red" }}>{playlist.name}</div>
      <button onClick={() => findSimilar(playlist.id)}></button>
      {/* <Playlist playlist={playlist} /> */}
    </div>
  );
}

export default App;
