import React, { useState, useEffect } from "react";
import Login from "./components/Login";
import Spotify from "./utils/spotify";
import "./styles/app.scss";

function App() {
  const [accessToken, setAccessToken] = useState(null);
  const [myDetails, setMyDetails] = useState({});

  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const getAccessToken = () => {
    const results = Spotify.getAccessToken();
    setAccessToken(results);
  };
  const getDetails = () => {
    const results = Spotify.getMyDetails();
    setMyDetails(results);
    console.log(results);
  };

  const searchHandler = (e) => {
    e.preventDefault();
    console.log(e.target.value);
    setSearchTerm(e.target.value);
  };

  const search = (e) => {
    e.preventDefault();
    Spotify.search(searchTerm).then((results) => {
      console.log(results);
      setSearchResults(results);
    });
  };

  return (
    <div className="App">
      <button onClick={getAccessToken}>Login</button>
      <button onClick={getDetails}>getDetails</button>
      <form>
        <input placeholder="search" onChange={(e) => searchHandler(e)}></input>
        <button onClick={search}>search</button>
        {searchTerm}
      </form>

      <ul>
        {searchResults &&
          searchResults.map((song) => {
            return (
              <li key={song.id} onClick={() => console.log(song.name)}>
                {song.name}
              </li>
            );
          })}
      </ul>
    </div>
  );
}

export default App;
