import React, { useState } from "react";
import { Input } from "semantic-ui-react";

export default function SliderWindow({
  getTopSongsSimilarArtists,
  sliderWindowOpen,
  setSliderWindowOpen,
}) {
  const [playlistName, setPlaylistName] = useState("");

  return (
    <div
      className={`slider-container ${sliderWindowOpen ? "slider-open" : ""}`}
    >
      <h2>Please name playlist</h2>
      <Input
        fluid
        placeholder="Playlist name"
        size="medium"
        onChange={(e) => setPlaylistName(e.target.value)}
      />
      <div className="btn-container">
        <button
          className={"btn success"}
          onClick={() => getTopSongsSimilarArtists(playlistName)}
        >
          Make Playlist
        </button>
        <button className={"btn cancle"}>cancle</button>
      </div>
    </div>
  );
}

