import React, { useState } from "react";
export default function SingleResult({ artist, index }) {
  const [setActive, setActiveState] = useState(false);

  const toggleAccordion = () => {
    console.log("toggle away");
    setActiveState(!setActive);
  };

  return (
    <div className={`result-container`} onClick={toggleAccordion}>
      <index className="index-wrapper">
        <div>{index}</div>
      </index>
      <img
        className="image-wrapper"
        src={artist.images.length > 0 ? artist.images[1].url : null}
        alt="artist-image"
      />
      <div className="text-wrapper">
        <h4 className="image-text">{artist.name}</h4>
      </div>
      <Button icon="ellipsis vertical" />
    </div>
  );
}
